package org.noos.xing.mydoggy.tutorial;

import info.clearthought.layout.TableLayout;
import javax.swing.*;

public class org.noos.xing.mydoggy.tutorial.SampleApp {
    private JFrame frame;

    protected void setUp() {
        initComponents();
    }

    protected void start() {
        SwingUtilities.invokeLater(new Runnable() {
            public void run() {
                frame.setVisible(true);
            }
        });
    }

    protected void initComponents() {
        // This is need to a correct visualization of all JPopupMenu.
        JPopupMenu.setDefaultLightWeightPopupEnabled(false);

        this.frame = new JFrame("Sample App...");
        this.frame.setSize(640, 480);
        this.frame.setLocation(100, 100);
        this.frame.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);

        // I love TableLayout. It's great.
        this.frame.getContentPane().setLayout(new TableLayout(new double[][]{{0, -1, 0}, {0, -1, 0}}));
    }

    public static void main(String[] args) {
        org.noos.xing.mydoggy.tutorial.SampleApp test = new org.noos.xing.mydoggy.tutorial.SampleApp();
        try {
            test.setUp();
            test.start();
        } catch (Exception e) {
            e.printStackTrace();
        }
    }
}
